import React, { useState } from "react";
import "./Faqs.css";
import { AiOutlinePlus } from "react-icons/ai";
import { FiX } from "react-icons/fi";
const Faqs = () => {
  const [showResults, setShowResults] = useState(false);
  const [show, setShow] = useState(true);
  const [showResults2, setShowResults2] = useState(false);
  const [show2, setShow2] = useState(true);
  const [showResults3, setShowResults3] = useState(false);
  const [show3, setShow3] = useState(true);
  const [showResults4, setShowResults4] = useState(false);
  const [show4, setShow4] = useState(true);
  const [showResults5, setShowResults5] = useState(false);
  const [show5, setShow5] = useState(true);
  const [showResults6, setShowResults6] = useState(false);
  const [show6, setShow6] = useState(true);

 
  return (
    <div className="faqspage">
      <h1 className="frequently">Frequently Asked Questions</h1>
      <h6 className="feelFree">
        Feel free to send us your questions or comments below
      </h6>
{/* Q1 and answer1 */}
      <hr className="line" />
      <h2 className="Q1">Lorem Ipsum?</h2>
      {show ? (
        <AiOutlinePlus
          className="show"
          onClick={() => {
            setShowResults(true);
            setShow(!show);
          }}
        />
      ) : (
        <FiX
          className="hide"
          onClick={() => {
            setShowResults(false);
            setShow(!show);
          }}
        />
      )}

      {showResults ? (
        <div className="answer1">
          <hr className="line" />
          <p>
            Contrary to popular belief, Lorem Ipsum is not simply random text.
            It has roots in a piece of classical Latin literature from 45 BC,
            making it over 2000 years old. Richard McClintock, a Latin professor
            at Hampden-Sydney College in Virginia, looked up one of the more
            obscure Latin words, consectetur from a Lorem Ipsum passage, and
            going through the cites of the word in classical literature
          </p>
          <hr className="line2" />
        </div>
      ) : null}
      <hr className="line" />
      <h2 className="Q2">Lorem Ipsum?</h2>
      {show2 ? (
        <AiOutlinePlus
          className="show2"
          onClick={() => {
            setShowResults2(true);
            setShow(!show);
          }}
        />
      ) : (
        <FiX
          className="hide"
          onClick={() => {
            setShowResults(false);
            setShow2(!show2);
          }}
        />
      )}

      {showResults ? (
        <div className="answer2">
          <hr className="line" />
          <p>
            Contrary to popular belief, Lorem Ipsum is not simply random text.
            It has roots in a piece of classical Latin literature from 45 BC,
            making it over 2000 years old. Richard McClintock, a Latin professor
            at Hampden-Sydney College in Virginia, looked up one of the more
            obscure Latin words, consectetur from a Lorem Ipsum passage, and
            going through the cites of the word in classical literature
          </p>
          <hr className="" />
        </div>
      ) : null}

      {/* <hr className="line" />
      <hr className="line" />
      <hr className="line" /> */}
    </div>
  );
};
export default Faqs;
